use std::time::Instant;

fn main() {
    let start = Instant::now();
    // Day 6: Tuning Trouble
    part1(); // part 1: 1142
    part2(); // part 2: 2803
    println!("Finished in: {:?}", start.elapsed())
}

fn part1() {
    let input = "";
    println!("part 1: {}", aoc_2022::utils::get_unique_substring_offset(input, 4) + input.len());
}

fn part2() {
    let input = "";
    println!("part 2: {}", aoc_2022::utils::get_unique_substring_offset(input, 14) + input.len());
}


#[test]
fn test_part1() {
    let test_inputs = ["mjqjpqmgbljsphdztnvjfqwrcgsmlb", "bvwbjplbgvbhsrlpgdmjqwftvncz", "nppdvjthqldpwncqszvftbrmjlhg", "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"];
    let expected_outputs = [7, 5, 6, 10, 11];
    for (i, test_input) in test_inputs.into_iter().enumerate() {
        println!("i={} {} -> {} vs expected: {}", i, test_input, get_unique_substring_offset_end(test_input, 4), expected_outputs[i]);
        assert_eq!(aoc_2022::utils::get_unique_substring_offset_end(test_input, 4), expected_outputs[i]);
    }
}

#[test]
fn test_part2() {
    let test_inputs = ["mjqjpqmgbljsphdztnvjfqwrcgsmlb", "bvwbjplbgvbhsrlpgdmjqwftvncz", "nppdvjthqldpwncqszvftbrmjlhg", "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"];
    let expected_outputs = [19, 23, 23, 29, 26];
    for (i, test_input) in test_inputs.into_iter().enumerate() {
        println!("i={} {} -> {} vs expected: {}", i, test_input, get_unique_substring_offset_end(test_input, 14), expected_outputs[i]);
        assert_eq!(aoc_2022::utils::get_unique_substring_offset_end(test_input, 14), expected_outputs[i]);
    }
}